import React from 'react'
import { useEffect, useState } from 'react'
function Banner({number}) {
    const [Banner, setBanner] = useState([])
    useEffect(() => {
        fetch("https://testapi.io/api/HoangHuy/https://testapi.io/api/HoangHuy/Banner")
        .then((res)=>res.json())
        .then((courses)=> setBanner(courses))
    },[number])
    console.log(Banner[1])
    return (
        <div className="Banner">
            <img className="Banner" src={Banner[number-1]} alt="Banner"></img>
        </div>
    )
}


export default Banner;
