import React from 'react'
import { useEffect, useRef } from 'react'
function Banner({ number }) {
    let Banner = useRef()
    useEffect(() => {
        fetch("https://testapi.io/api/HoangHuy/https://testapi.io/api/HoangHuy/Banner")
        .then((courses) => {
            return courses.json();
        })
        .then((posts) => {
            return Banner.current = posts
        })
    }, [number])
    console.log(Banner.current)
    return ( 
        <div>        
            <div className="Banner">
                <img className="Banner" src={""} alt="Banner"></img>
            </div>
        </div>
    )

}


export default Banner;
