import React from 'react'
import Infoitem from './Infoitem'
import { useEffect, useState } from 'react';
const Pages = ['Wesushi', 'Wecake', 'Wetea', 'Weshop', 'Westyle', 'Wescan', 'Wefix', 'Welotto']
function Item({number}) {
        useEffect(() => {
            const a = document.querySelectorAll(".img")
            console.log(a)
            a.forEach(element => {
                element.onclick = function () {
                    
                    this.innerHTML = <Infoitem/>
                }
            });
        })
    const [data, setdata] = useState([])
    useEffect(() => {
        fetch(`https://621089284cd3049e177eb0c8.mockapi.io/${Pages[number-1]}`)
            .then((courses) => {
                return courses.json();
            })
            .then((posts) => {
                return setdata(posts)
            })
    }, [number])
    return (
        <div className="row">
            {data.map((data,i) => (
                <div className="col" key={data.id}>
                    <div className="item" data-id={i}>
                        <div>
                            <img className="img" src={data.url} alt="img"></img>
                        </div>
                        <div className="title">
                            <h1 className="title">{data.title}</h1>
                        </div>
                        <div className="price">
                            <h1 className="price">{data.price}</h1>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Item;
