import React from 'react'
import { useEffect, useState } from 'react'
function Banner({ number }) {
    const [Banner, setBanner] = useState([])
   
        fetch("https://testapi.io/api/HoangHuy/https://testapi.io/api/HoangHuy/Banner")
        .then((courses) => {
            return courses.json();
        })
        .then((posts) => {
            return setBanner(posts)
        })

    console.log(Banner[number-1])
    return ( 
        <div>
            <div className="Banner">
                <img className="Banner" src="" alt="Banner"></img>
            </div>
        </div>
    )

}


export default Banner;
