import React from 'react'
import { useEffect, useState } from 'react';
function Item({number}) {
    const [data, setdata] = useState([])
    const Pages = ['Wesushi', 'Wecake', 'Wetea', 'Weshop', 'Westyle', 'Wescan', 'Wefix', 'Welotto']
    useEffect(() => {
        fetch(`https://testapi.io/api/HoangHuy/https://testapi.io/api/HoangHuy/${Pages[number-1]}`)
            .then((courses) => {
                return courses.json();
            })
            .then((posts) => {
                return setdata(posts)
            })
    }, [])
    return (
        <div className="row">
            {data.map(data => (
                <div className="col" key={data.id}>
                    <div className="item">
                        <div className="img">
                            <img className="img" src={data.url} alt="img"></img>
                        </div>
                        <div className="title">
                            <h1 className="title">{data.title}</h1>
                        </div>
                        <div className="price">
                            <h1 className="price">{data.price}</h1>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Item;
