import React from 'react'
import { useEffect, useState } from 'react'
function Banner({number}) {
    const [Banner, setBanner] = useState()
    useEffect(() => {
        fetch("https://testapi.io/api/HoangHuy/Banner")
        .then((res)=>res.json())
        .then((courses)=> setBanner(courses))
    })

    return (
        <div className="Banner">
            <img className="Banner" src={Banner[number].url} alt="Banner"></img>
        </div>
    )
}


export default Banner;
