import React from 'react'
import Infoitem from './Infoitem'
import { useEffect, useState } from 'react';
const Pages = ['Wesushi', 'Wecake', 'Wetea', 'Weshop', 'Westyle', 'Wescan', 'Wefix', 'Welotto']
function Item({number}) {
    const [data, setdata] = useState([])
    useEffect(() => {
        fetch(`https://testapi.io/api/HoangHuy/https://testapi.io/api/HoangHuy/${Pages[number-1]}`)
            .then((courses) => {
                return courses.json();
            })
            .then((posts) => {
                return setdata(posts)
            })
    }, [number])
    return (
        <div className="row">
            {data.map((data,i) => (
                <div className="col" key={data.id}>
                    <div className="item" data-id={i}>
                        <div className="img" onClick={Infoitem}>
                            <img className="img" src={data.url} alt="img"></img>
                        </div>
                        <div className="title">
                            <h1 className="title">{data.title}</h1>
                        </div>
                        <div className="price">
                            <h1 className="price">{data.price}</h1>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Item;
