import React from 'react'
import { useEffect, useState } from 'react';
import Infoitem from "./Infoitem"

const Pages = ['Wesushi', 'Wecake', 'Wetea', 'Weshop', 'Westyle', 'Wescan', 'Wefix', 'Welotto']
function Item({ number }) {
    const h = document.querySelectorAll(".img")
    h.forEach((tab,i) => {
        tab.onclick = () => {
            console.log(i)
        }
    })
    const handleClickres = () => {
        const a = document.querySelector('.containerinfo')
        a.classList.add('open')
    }
    const [data, setdata] = useState([])
    useEffect(() => {
        fetch(`https://621089284cd3049e177eb0c8.mockapi.io/${Pages[number - 1]}`)
            .then((courses) => {
                return courses.json();
            })
            .then((posts) => {
                return setdata(posts)
            })
    }, [number])
    return (

        <div className="row">
            <div className="containerinfo">
                <Infoitem />
            </div>
            {data.map((data, i) => (

                <div className="col" key={data.id}>
                    
                    <div className="item" >
                        <div className="infoproduct">

                        </div>
                        
                        <div>
                            <img data-id={i} className="img" onClick={handleClickres} src={data.url} alt="img"></img>
                        </div>
                        <div className="title">
                            <h1 className="title">{data.title}</h1>
                        </div>
                        <div className="price">
                            <h1 className="price">{data.price}</h1>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Item;
