import React from 'react'
import { useEffect, useState } from 'react'
function Banner({ number }) {
    const [Banner, setBanner] = useState([])
    useEffect(() => {
        fetch("https://testapi.io/api/HoangHuy/https://testapi.io/api/HoangHuy/Banner")
            .then((res) => res.json())
            .then((courses) => setBanner(courses))
    }, [number])

    return (
        <div>

            {Banner.map((data) => (<div key={data.id}className="Banner">
                <img className="Banner" src={data.url} alt="Banner"></img>
            </div>))

            }
        </div>
    )
}


export default Banner;
